using Microsoft.AspNetCore.Identity;
using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using WorldExplorerEurope.API.Domain;
using WorldExplorerEurope.API.Domain.Models;
using WorldExplorerEurope.API.Domain.Services;

namespace WorldExplorerEurope.API.Data
{
    public class WorldExplorerContext : DbContext
    {
        private readonly RestcountriesService _restcountriesService;
        public WorldExplorerContext(DbContextOptions<WorldExplorerContext> options) : base(options)
        {
            _restcountriesService = new RestcountriesService();
        }

        public DbSet<Country> Countries { get; set; }
        public DbSet<User> Users { get; set; }
        public DbSet<Favourite> Favourites { get; set; }
        public DbSet<PhotoMemory> Memories { get; set; }
        public DbSet<SpotifyPlaylist> SpotifyPlaylists { get; set; }
        public DbSet<Wishlist> Wishlists { get; set; }

        public DbSet<CountryPhotoMemories> CountryPhotoMemories { get; set; }
        public DbSet<CountryFavourites> CountryFavourites { get; set; }
        public DbSet<CountryWishlists> CountryWishlists { get; set; }

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            modelBuilder.Entity<CountryPhotoMemories>().ToTable("CountryPhotoMemories")
                .HasKey(ag => new { ag.CountryId, ag.MemoryId });

            modelBuilder.Entity<CountryPhotoMemories>()
                .HasOne(ag => ag.Country)
                .WithMany(m => m.Memories)
                .HasForeignKey(ag => ag.CountryId);

            modelBuilder.Entity<CountryPhotoMemories>()
                .HasOne(ag => ag.Memory)
                .WithMany(g => g.Memories)
                .HasForeignKey(ag => ag.MemoryId);

            modelBuilder.Entity<CountryFavourites>().ToTable("CountryFavourites")
                .HasKey(ag => new { ag.CountryId, ag.FavouriteId });

            modelBuilder.Entity<CountryFavourites>()
                .HasOne(ag => ag.Country)
                .WithMany(g => g.Favourites)
                .HasForeignKey(ag => ag.CountryId);

            modelBuilder.Entity<CountryFavourites>()
                .HasOne(ag => ag.Favourite)
                .WithMany(m => m.Favourites)
                .HasForeignKey(ag => ag.FavouriteId);

            modelBuilder.Entity<CountryWishlists>().ToTable("CountryWishlists")
                .HasKey(ag => new { ag.CountryId, ag.WishlistId });

            modelBuilder.Entity<CountryWishlists>()
                .HasOne(ag => ag.Country)
                .WithMany(m => m.CountryWishlists)
                .HasForeignKey(ag => ag.CountryId);

            modelBuilder.Entity<CountryWishlists>()
                .HasOne(ag => ag.Wishlist)
                .WithMany(m => m.Wishlists)
                .HasForeignKey(ag => ag.WishlistId);


            var countries = _restcountriesService.GetCountryData();

            #region countries

            /*I chose to to it this way to make it easier
             * to find any mistakes
             */

            List<Guid> guids = new List<Guid>();
            foreach (var country in countries)
            {
                guids.Add(Guid.NewGuid());
            }

            modelBuilder.Entity<Country>()
                .ToTable("Countries")
                .HasData(
                new Country { Id = guids[0], Name = countries[0].name, Capital = countries[0].capital, Currency = countries[0].currencies[0].name, Description = "The Åland Islands or Åland is an archipelago province at the entrance to the Gulf of Bothnia in the Baltic Sea belonging to Finland. It is autonomous, demilitarised and is the only monolingually Swedish-speaking region in Finland. It is the smallest region of Finland, constituting 0.51% of its land area and 0.54% of its population.", Flag = new Uri(countries[0].flag), Population = countries[0].population, IsEUMember = true },
                new Country { Id = guids[1], Name = countries[1].name, Capital = countries[1].capital, Currency = countries[1].currencies[0].name, Description = "Albania, officially the Republic of Albania, is a country in Southeast Europe on the Adriatic and Ionian Sea within the Mediterranean Sea. It shares land borders with Montenegro to the northwest, Kosovo to the northeast, North Macedonia to the east, Greece to the south and maritime borders with Greece, Montenegro and Italy to the west.", Flag = new Uri(countries[1].flag), Population = countries[1].population, IsEUMember = true },
                new Country { Id = guids[2], Name = countries[2].name, Capital = countries[2].capital, Currency = countries[2].currencies[0].name, Description = "Andorra, officially the Principality of Andorra, also called the Principality of the Valleys of Andorra, is a sovereign landlocked microstate on the Iberian Peninsula, in the eastern Pyrenees, bordered by France to the north and Spain to the south. Believed to have been created by Charlemagne, Andorra was ruled by the count of Urgell until 988, when it was transferred to the Roman Catholic Diocese of Urgell. The present principality was formed by a charter in 1278. It is known as a principality as it is a diarchy headed by two princes: the Catholic bishop of Urgell in Catalonia, Spain, and the president of the French Republic.", Flag = new Uri(countries[2].flag), Population = countries[2].population, IsEUMember = true },
                new Country { Id = guids[3], Name = countries[3].name, Capital = countries[3].capital, Currency = countries[3].currencies[0].name, Description = "Austria, officially the Republic of Austria, is a landlocked East Alpine country in the southern part of Central Europe. It is composed of nine federated states (Bundesländer), one of which is Vienna, Austria's capital and its largest city. It is bordered by Germany to the northwest, Czech Republic to the north, Slovakia to the northeast, Hungary to the east, Slovenia and Italy to the south, and Switzerland and Liechtenstein to the west. Austria occupies an area of 83,879 km2 (32,386 sq mi) and has a population of nearly 9 million people. While German is the country's official language, many Austrians communicate informally in a variety of Bavarian dialects.", Flag = new Uri(countries[3].flag), Population = countries[3].population, IsEUMember = true },
                new Country { Id = guids[4], Name = countries[4].name, Capital = countries[4].capital, Currency = countries[4].currencies[0].name, Description = "Belarus, officially the Republic of Belarus, formerly known by its Russian name Byelorussia or Belorussia, is a landlocked country in Eastern Europe bordered by Russia to the northeast, Ukraine to the south, Poland to the west, and Lithuania and Latvia to the northwest. Its capital and most populous city is Minsk. Over 40% of its 207,600 square kilometres (80,200 sq mi) is forested. Its major economic sectors are service industries and manufacturing. Until the 20th century, different states at various times controlled the lands of modern-day Belarus, including the Principality of Polotsk, the Grand Duchy of Lithuania, the Polish–Lithuanian Commonwealth, and the Russian Empire.", Flag = new Uri(countries[4].flag), Population = countries[4].population, IsEUMember = true },
                new Country { Id = guids[5], Name = countries[5].name, Capital = countries[5].capital, Currency = countries[5].currencies[0].name, Description = "Belgium, officially the Kingdom of Belgium, is a country in Western Europe. It is bordered by the Netherlands to the north, Germany to the east, Luxembourg to the southeast, France to the southwest, and the North Sea to the northwest. It covers an area of 30,689 km2 (11,849 sq mi) and has a population of more than 11.5 million, making it the 22nd most densely populated country in the world and the 6th most densely populated country in Europe, with a density of 376 per square kilometre (970/sq mi). The capital and largest city is Brussels; other major cities are Antwerp, Ghent, Charleroi and Liège.", Flag = new Uri(countries[5].flag), Population = countries[5].population, IsEUMember = true },
                new Country { Id = guids[6], Name = countries[6].name, Capital = countries[6].capital, Currency = countries[6].currencies[0].name, Description = "Bosnia and Herzegovina, abbreviated BiH or B&H, sometimes called Bosnia–Herzegovina and often known informally as Bosnia, is a country of South and Southeast Europe, located within the Balkans. Sarajevo is the capital and largest city.", Flag = new Uri(countries[6].flag), Population = countries[6].population, IsEUMember = true },
                new Country { Id = guids[7], Name = countries[7].name, Capital = countries[7].capital, Currency = countries[7].currencies[0].name, Description = "Bulgaria, officially the Republic of Bulgaria, is a country in Southeast Europe. It is bordered by Romania to the north, Serbia and North Macedonia to the west, Greece and Turkey to the south, and the Black Sea to the east. The capital and largest city is Sofia; other major cities are Plovdiv, Varna and Burgas. With a territory of 110,994 square kilometres (42,855 sq mi), Bulgaria is Europe's 16th-largest country.", Flag = new Uri(countries[7].flag), Population = countries[7].population, IsEUMember = true },
                new Country { Id = guids[8], Name = countries[8].name, Capital = countries[8].capital, Currency = countries[8].currencies[0].name, Description = "Croatia, officially the Republic of Croatia, is a country in Southeast Europe. It borders Slovenia to the northwest, Hungary to the northeast, Serbia to the east, Bosnia and Herzegovina, and Montenegro to the southeast, sharing a maritime border with Italy. Its capital, Zagreb, forms one of the country's primary subdivisions, along with twenty counties. Croatia has an area of 56,594 square kilometres and a population of 4.28 million, most of whom are Roman Catholics.", Flag = new Uri(countries[8].flag), Population = countries[8].population, IsEUMember = true },
                new Country { Id = guids[9], Name = countries[9].name, Capital = countries[9].capital, Currency = countries[9].currencies[0].name, Description = "Cyprus, officially the Republic of Cyprus, is an island country in the Eastern Mediterranean and the third-largest and third-most populous island in the Mediterranean, located south of Turkey, west of Syria and Lebanon, north of Israel, the Gaza Strip and Egypt, and southeast of Greece.", Flag = new Uri(countries[9].flag), Population = countries[9].population, IsEUMember = true },
                new Country { Id = guids[10], Name = countries[10].name, Capital = countries[10].capital, Currency = countries[10].currencies[0].name, Description = "The Czech Republic, also known by its short-form name, Czechia, is a landlocked country in Central Europe bordered by Poland to the northeast, Slovakia to the southeast, Austria to the south, and Germany to the west. The Czech Republic has hilly landscape that covers an area of 78,866 square kilometers (30,450 sq mi) with a mostly temperate continental climate and oceanic climate. It is a unitary parliamentary republic, with 10.7 million inhabitants. Its capital and largest city is Prague, with 1.3 million residents; other major cities are Brno, Ostrava, Olomouc and Pilsen.", Flag = new Uri(countries[10].flag), Population = countries[10].population, IsEUMember = true },
                new Country { Id = guids[11], Name = countries[11].name, Capital = countries[11].capital, Currency = countries[11].currencies[0].name, Description = "Denmark, officially the Kingdom of Denmark, is a Nordic country in Northwest Europe. Denmark proper, which is the southernmost of the Scandinavian countries, consists of a peninsula, Jutland, and an archipelago of 443 named islands, with the largest being Zealand, Funen and the North Jutlandic Island. The islands are characterised by flat, arable land and sandy coasts, low elevation and a temperate climate. The southernmost of the Scandinavian nations, Denmark lies southwest of Sweden and south of Norway, and is bordered to the south by Germany. The Kingdom of Denmark also includes two autonomous territories in the North Atlantic Ocean: the Faroe Islands and Greenland. Denmark has a total area of 42,924 km2 (16,573 sq mi), land area of 42,394 km2 (16,368 sq mi), and the total area including Greenland and the Faroe Islands is 2,210,579 km2 (853,509 sq mi), and a population of 5.8 million in Denmark proper.", Flag = new Uri(countries[11].flag), Population = countries[11].population, IsEUMember = true },
                new Country { Id = guids[12], Name = countries[12].name, Capital = countries[12].capital, Currency = countries[12].currencies[0].name, Description = "Estonia, officially the Republic of Estonia, is a country on the eastern coast of the Baltic Sea in Northern Europe. It is bordered to the north by the Gulf of Finland with Finland on the other side, to the west by the Baltic Sea with Sweden on the other side, to the south by Latvia (343 km), and to the east by Lake Peipus and Russia (338.6 km). The territory of Estonia consists of the mainland and of 2,222 islands in the Baltic Sea, covering a total area of 45,227 km2 (17,462 sq mi), water 2,839 km2 (1,096 sq mi), land area 42,388 km2 (16,366 sq mi), and is influenced by a humid continental climate. The official language of the country, Estonian, is the second-most-spoken Finnic language.", Flag = new Uri(countries[12].flag), Population = countries[12].population, IsEUMember = true },
                new Country { Id = guids[13], Name = countries[13].name, Capital = countries[13].capital, Currency = countries[13].currencies[0].name, Description = "The Faroe Islands, , is a North Atlantic archipelago located 320 kilometres (200 mi) north-northwest of Scotland, and about halfway between Norway and Iceland. It is an autonomous territory within the Kingdom of Denmark. The islands have a total area of about 1,400 square kilometres (540 sq mi) with a population of 51,783 as of June 2019.", Flag = new Uri(countries[13].flag), Population = countries[13].population, IsEUMember = true },
                new Country { Id = guids[14], Name = countries[14].name, Capital = countries[14].capital, Currency = countries[14].currencies[0].name, Description = "Finland, officially the Republic of Finland, is a Nordic country in Northern Europe bordering the Baltic Sea, Gulf of Bothnia, and Gulf of Finland, between Sweden to the west, Russia to the east, Estonia to the south, and north-eastern Norway to the north. The capital and largest city is Helsinki. Other major cities are Espoo, Tampere, Vantaa, Oulu, Turku, Jyväskylä, Lahti and Kuopio.", Flag = new Uri(countries[14].flag), Population = countries[14].population, IsEUMember = true },
                new Country { Id = guids[15], Name = countries[15].name, Capital = countries[15].capital, Currency = countries[15].currencies[0].name, Description = "France, officially the French Republic, is a country whose territory consists of metropolitan France in Western Europe and several overseas regions and territories. The metropolitan area of France extends from the Mediterranean Sea to the English Channel and the North Sea, and from the Rhine to the Atlantic Ocean. It is bordered by Belgium, Luxembourg and Germany to the northeast, Switzerland and Italy to the east, and Andorra and Spain to the south. The overseas territories include French Guiana in South America and several islands in the Atlantic, Pacific and Indian oceans. The country's 18 integral regions span a combined area of 643,801 square kilometres (248,573 sq mi) and a total population of 67.02 million. France is a unitary semi-presidential republic with its capital in Paris, the country's largest city and main cultural and commercial centre. Other major urban areas include Lyon, Marseille, Toulouse, Bordeaux, Lille and Nice. France, including its overseas territories, has the most number of time zones of any country, with a total of 12.", Flag = new Uri(countries[15].flag), Population = countries[15].population, IsEUMember = true },
                new Country { Id = guids[16], Name = countries[16].name, Capital = countries[16].capital, Currency = countries[16].currencies[0].name, Description = "Germany, constitutionally the Federal Republic of Germany, is a country in Central and Western Europe. Covering an area of 357,022 square kilometres (137,847 sq mi), it lies between the Baltic and North seas to the north, and the Alps and Lake Constance to the south. It borders Denmark to the north, Poland and the Czech Republic to the east, Austria and Switzerland to the south, and France, Luxembourg, Belgium, and the Netherlands to the west. With 83 million inhabitants of its 16 constituent states, it is the second-most populous country in Europe after Russia, as well as the most populous member state of the European Union. Its capital and largest city is Berlin, while its largest urban area is the Ruhr. The country's financial capital is Frankfurt; other major cities include Hamburg, Munich, Cologne, and Stuttgart.", Flag = new Uri(countries[16].flag), Population = countries[16].population, IsEUMember = true },
                new Country { Id = guids[17], Name = countries[17].name, Capital = countries[17].capital, Currency = countries[17].currencies[0].name, Description = "Gibraltar is a British Overseas Territory located at the southern tip of the Iberian Peninsula. It has an area of 6.7 km2 (2.6 sq mi) and is bordered to the north by Spain. The landscape is dominated by the Rock of Gibraltar at the foot of which is a densely populated town area, home to over 32,000 people, primarily Gibraltarians.", Flag = new Uri(countries[17].flag), Population = countries[17].population, IsEUMember = true },
                new Country { Id = guids[18], Name = countries[18].name, Capital = countries[18].capital, Currency = countries[18].currencies[0].name, Description = "Gibraltar is a British Overseas Territory located at the southern tip of the Iberian Peninsula. It has an area of 6.7 km2 (2.6 sq mi) and is bordered to the north by Spain. The landscape is dominated by the Rock of Gibraltar at the foot of which is a densely populated town area, home to over 32,000 people, primarily Gibraltarians.", Flag = new Uri(countries[18].flag), Population = countries[18].population, IsEUMember = true },
                new Country { Id = guids[19], Name = countries[19].name, Capital = countries[19].capital, Currency = countries[19].currencies[0].name, Description = "Guernsey is an island in the English Channel off the coast of Normandy. It lies roughly north of Saint-Malo and to the west of Jersey and the Cotentin Peninsula. With several smaller nearby islands, it forms a jurisdiction within the Bailiwick of Guernsey, a British Crown dependency. The jurisdiction is made up of ten parishes on the island of Guernsey, three other inhabited islands, and many small islets and rocks.", Flag = new Uri(countries[19].flag), Population = countries[19].population, IsEUMember = true },
                new Country { Id = guids[20], Name = countries[20].name, Capital = countries[20].capital, Currency = countries[20].currencies[0].name, Description = "The Holy See, also called the See of Rome, is the jurisdiction of the Bishop of Rome, known as the pope, which includes the apostolic episcopal see of the Diocese of Rome with universal ecclesiastical jurisdiction of the worldwide Catholic Church, as well as a sovereign entity of international law.", Flag = new Uri(countries[20].flag), Population = countries[20].population, IsEUMember = true },
                new Country { Id = guids[21], Name = countries[21].name, Capital = countries[21].capital, Currency = countries[21].currencies[0].name, Description = "Hungary is a country in Central Europe. Spanning 93,030 square kilometres (35,920 sq mi) in the Carpathian Basin, it borders Slovakia to the north, Ukraine to the northeast, Romania to the east and southeast, Serbia to the south, Croatia and Slovenia to the southwest, and Austria to the west. With about 10 million inhabitants, Hungary is a medium-sized member state of the European Union. The official language is Hungarian, which is the most widely spoken Uralic language in the world, and among the few non-Indo-European languages to be widely spoken in Europe. Hungary's capital and largest city is Budapest; other major urban areas include Debrecen, Szeged, Miskolc, Pécs, and Győr.", Flag = new Uri(countries[21].flag), Population = countries[21].population, IsEUMember = true },
                new Country { Id = guids[22], Name = countries[22].name, Capital = countries[22].capital, Currency = countries[22].currencies[0].name, Description = "Iceland is a Nordic island country in the North Atlantic, with a population of 364,260 and an area of 103,000 km2 (40,000 sq mi), making it the most sparsely populated country in Europe. The capital and largest city is Reykjavík. Reykjavik and the surrounding areas in the southwest of the country are home to over two-thirds of the population. Iceland is volcanically and geologically active. The interior consists of a plateau characterised by sand and lava fields, mountains, and glaciers, and many glacial rivers flow to the sea through the lowlands. Iceland is warmed by the Gulf Stream and has a temperate climate, despite a high latitude just outside the Arctic Circle. Its high latitude and marine influence keep summers chilly, with most of the archipelago having a polar climate.", Flag = new Uri(countries[22].flag), Population = countries[22].population, IsEUMember = true },
                new Country { Id = guids[23], Name = countries[23].name, Capital = countries[23].capital, Currency = countries[23].currencies[0].name, Description = "Ireland is an island in the North Atlantic. It is separated from Great Britain to its east by the North Channel, the Irish Sea, and St George's Channel. Ireland is the second-largest island of the British Isles, the third-largest in Europe, and the twentieth-largest on Earth.", Flag = new Uri(countries[23].flag), Population = countries[23].population, IsEUMember = true },
                new Country { Id = guids[24], Name = countries[24].name, Capital = countries[24].capital, Currency = countries[24].currencies[0].name, Description = "The Isle of Man, also known as Mann, is a self-governing British Crown dependency situated in the Irish Sea between Great Britain and Ireland. The head of state, Queen Elizabeth II, holds the title of Lord of Mann and is represented by a lieutenant governor. The United Kingdom has responsibility for the island's defence.", Flag = new Uri(countries[24].flag), Population = countries[24].population, IsEUMember = true },
                new Country { Id = guids[25], Name = countries[25].name, Capital = countries[25].capital, Currency = countries[25].currencies[0].name, Description = "Italy, officially the Italian Republic, is a European country consisting of a peninsula delimited by the Alps and surrounded by several islands. Italy is located in south-central Europe, and it is also considered a part of western Europe. A unitary parliamentary republic with its capital in Rome, the country covers a total area of 301,340 km2 (116,350 sq mi) and shares land borders with France, Switzerland, Austria, Slovenia, and the enclaved microstates of Vatican City and San Marino. Italy has a territorial exclave in Switzerland (Campione) and a maritime exclave in the Tunisian Sea (Lampedusa). With around 60 million inhabitants, Italy is the third-most populous member state of the European Union.", Flag = new Uri(countries[25].flag), Population = countries[25].population, IsEUMember = true },
                new Country { Id = guids[26], Name = countries[26].name, Capital = countries[26].capital, Currency = countries[26].currencies[0].name, Description = "Jersey, officially the Bailiwick of Jersey, is a British Crown dependency located near the coast of Normandy, France. It is the second-closest of the Channel Islands to France, after Alderney.", Flag = new Uri(countries[26].flag), Population = countries[26].population, IsEUMember = true },
                new Country { Id = guids[27], Name = countries[27].name, Capital = countries[27].capital, Currency = countries[27].currencies[0].name, Description = "Latvia, officially known as the Republic of Latvia, is a country in the Baltic region of Northern Europe. Since its independence, Latvia has been referred to as one of the Baltic states. It is bordered by Estonia to the north, Lithuania to the south, Russia to the east, Belarus to the southeast, and shares a maritime border with Sweden to the west. Latvia has 1,957,200 inhabitants and a territory of 64,589 km2 (24,938 sq mi). The country has a temperate seasonal climate. The Baltic Sea moderates climate, although it has four distinct seasons and snowy winters.", Flag = new Uri(countries[27].flag), Population = countries[27].population, IsEUMember = true },
                new Country { Id = guids[28], Name = countries[28].name, Capital = countries[28].capital, Currency = countries[28].currencies[0].name, Description = "Liechtenstein, officially the Principality of Liechtenstein, is a German-speaking microstate situated in the Alps and in the southwest of Central Europe. The principality is a semi-constitutional monarchy headed by the Prince of Liechtenstein; the Prince's extensive powers are equivalent to those of a President in a semi-presidential system.", Flag = new Uri(countries[28].flag), Population = countries[28].population, IsEUMember = true },
                new Country { Id = guids[29], Name = countries[29].name, Capital = countries[29].capital, Currency = countries[29].currencies[0].name, Description = "Lithuania, officially the Republic of Lithuania, is a country in the Baltic region of Europe. Lithuania is considered to be one of the Baltic states. The country is situated along the southeastern shore of the Baltic Sea, to the east of Sweden and Denmark. It is bordered by Latvia to the north, Belarus to the east and south, Poland to the south, and Kaliningrad Oblast to the southwest. Lithuania has an estimated population of 2.8 million people as of 2019, and its capital and largest city is Vilnius. Other major cities are Kaunas and Klaipėda. Lithuanians are Baltic people. The official language, Lithuanian, is one of only two living languages in the Baltic branch of the Indo-European language family, the other being Latvian.", Flag = new Uri(countries[29].flag), Population = countries[29].population, IsEUMember = true },
                new Country { Id = guids[30], Name = countries[30].name, Capital = countries[30].capital, Currency = countries[30].currencies[0].name, Description = "Luxembourg, officially the Grand Duchy of Luxembourg, is a landlocked country in western Europe. It is bordered by Belgium to the west and north, Germany to the east, and France to the south. Its capital, Luxembourg City, is one of the four official capitals of the European Union and the seat of the European Court of Justice, the highest judicial authority in the EU. Its culture, people, and languages are highly intertwined with its neighbours, making it essentially a mixture of French and German cultures, as evident by the nation's three official languages: French, German, and the national language of Luxembourgish. The repeated invasions by Germany, especially in World War II, resulted in the country's strong will for mediation between France and Germany and, among other things, led to the foundation of the European Union.", Flag = new Uri(countries[30].flag), Population = countries[30].population, IsEUMember = true },
                new Country { Id = guids[31], Name = countries[31].name, Capital = countries[31].capital, Currency = countries[31].currencies[0].name, Description = "Macedonia most commonly refers to:North Macedonia, a country in southeastern Europe, formerly known as the Republic of Macedonia\nMacedonia, a kingdom in Greek antiquity\nMacedonia (Greece), a traditional geographic region spanning three administrative divisions of northern Greece\nMacedonia (region), a geographic and historical region that today includes parts of six Balkan countries ", Flag = new Uri(countries[31].flag), Population = countries[31].population, IsEUMember = true },
                new Country { Id = guids[32], Name = countries[32].name, Capital = countries[32].capital, Currency = countries[32].currencies[0].name, Description = "Malta, officially known as the Republic of Malta, is a Southern European island country consisting of an archipelago in the Mediterranean Sea. It lies 80 km (50 mi) south of Italy, 284 km (176 mi) east of Tunisia, and 333 km (207 mi) north of Libya. With a population of about 475,000 over an area of 316 km2 (122 sq mi), Malta is the world's tenth smallest in area and fifth most densely populated sovereign country. Its capital is Valletta, which is the smallest national capital in the European Union by area at 0.8 km2 (0.31 sq mi). The official and national language is Maltese, which is descended from Sicilian Arabic that developed during the Emirate of Sicily, while English serves as the second official language.", Flag = new Uri(countries[32].flag), Population = countries[32].population, IsEUMember = true },
                new Country { Id = guids[33], Name = countries[33].name, Capital = countries[33].capital, Currency = countries[33].currencies[0].name, Description = "Moldova, officially the Republic of Moldova, is a landlocked country in Eastern Europe, bordered by Romania to the west and Ukraine to the north, east, and south. The capital city is Chișinău.", Flag = new Uri(countries[33].flag), Population = countries[33].population, IsEUMember = true },
                new Country { Id = guids[34], Name = countries[34].name, Capital = countries[34].capital, Currency = countries[34].currencies[0].name, Description = "Monaco, officially the Principality of Monaco, is a sovereign city-state, country, and microstate on the French Riviera in Western Europe. France borders the country on three sides while the other side borders the Mediterranean Sea. Monaco is about 15 km (9.3 mi) from the state border with Italy.", Flag = new Uri(countries[34].flag), Population = countries[34].population, IsEUMember = true },
                new Country { Id = guids[35], Name = countries[35].name, Capital = countries[35].capital, Currency = countries[35].currencies[0].name, Description = "Montenegro is a country of South and Southeast Europe on the coast of the Balkans. It borders Bosnia-Herzegovina to the northwest, Serbia to the northeast, Kosovo to the east, Albania to the southeast, the Adriatic Sea to the southwest, and Croatia to the west. Montenegro has an area of 13,812 square kilometres and a population of 620,079. Its capital, Podgorica, is one of the twenty-three municipalities in the country. Cetinje is designated as the Old Royal Capital.", Flag = new Uri(countries[35].flag), Population = countries[35].population, IsEUMember = true },
                new Country { Id = guids[36], Name = countries[36].name, Capital = countries[36].capital, Currency = countries[36].currencies[0].name, Description = "The Netherlands, informally Holland, is a country in Northwestern Europe with some overseas territories in the Caribbean. In Europe, it consists of 12 provinces that border Germany to the east, Belgium to the south, and the North Sea to the northwest, with maritime borders in the North Sea with those countries and the United Kingdom. Together with the Caribbean Netherlands —Bonaire, Sint Eustatius and Saba—it forms a constituent country of the Kingdom of the Netherlands. The official language is Dutch and a secondary official language in the province of Friesland is West Frisian. In the north and east of the country, Low Saxon is also spoken, and in the southeast, Limburgish. In the Caribbean Netherlands English and Papiamento are recognised languages.", Flag = new Uri(countries[36].flag), Population = countries[36].population, IsEUMember = true },
                new Country { Id = guids[37], Name = countries[37].name, Capital = countries[37].capital, Currency = countries[37].currencies[0].name, Description = "Norway, officially the Kingdom of Norway, is a Nordic country in Northwestern Europe whose territory comprises the western and northernmost portion of the Scandinavian Peninsula; the remote island of Jan Mayen and the archipelago of Svalbard are also part of the Kingdom of Norway. The sub-Antarctic Bouvet Island is a dependent territory and thus not considered part of the kingdom. Norway also lays claim to the Antarctic territories of Queen Maud Land and Peter I Island.", Flag = new Uri(countries[37].flag), Population = countries[37].population, IsEUMember = true },
                new Country { Id = guids[38], Name = countries[38].name, Capital = countries[38].capital, Currency = countries[38].currencies[0].name, Description = "Poland, officially the Republic of Poland, is a country located in Central Europe. It is divided into 16 administrative subdivisions, covering an area of 312,696 square kilometres (120,733 sq mi), and has a largely temperate seasonal climate. With a population of nearly 38.5 million people, Poland is the fifth most populous member state of the European Union. Poland's capital and largest metropolis is Warsaw. Other major cities include Kraków, Łódź, Wrocław, Poznań, Gdańsk, and Szczecin.", Flag = new Uri(countries[38].flag), Population = countries[38].population, IsEUMember = true },
                new Country { Id = guids[39], Name = countries[39].name, Capital = countries[39].capital, Currency = countries[39].currencies[0].name, Description = "Portugal, officially the Portuguese Republic, is a country located mostly on the Iberian Peninsula, in southwestern Europe. It is the westernmost sovereign state of mainland Europe, being bordered to the west and south by the Atlantic Ocean and to the north and east by Spain. Its territory also includes the Atlantic archipelagos of the Azores and Madeira, both autonomous regions with their own regional governments.", Flag = new Uri(countries[39].flag), Population = countries[39].population, IsEUMember = true },
                new Country { Id = guids[40], Name = countries[40].name, Capital = countries[40].capital, Currency = countries[40].currencies[0].name, Description = "Kosovo, officially the Republic of Kosovo, is a partially-recognized state in Southeast Europe, subject to a territorial dispute with the Republic of Serbia.", Flag = new Uri(countries[40].flag), Population = countries[40].population, IsEUMember = true },
                new Country { Id = guids[41], Name = countries[41].name, Capital = countries[41].capital, Currency = countries[41].currencies[0].name, Description = "Romania is a country located at the crossroads of Central, Eastern, and Southeastern Europe. It borders on the Black Sea to the southeast, and with Bulgaria to the south, Ukraine to the north, Hungary to the west, Serbia to the southwest, and Moldova to the east. It has a predominantly temperate-continental climate. With a total area of 238,397 square kilometres, Romania is the 12th-largest country in Europe and the 6th most populous member state of the European Union, having approximately 20 million inhabitants. Its capital and largest city is Bucharest. Other major urban areas include: Cluj-Napoca, Timișoara, Iași, Constanța, Craiova, Brașov, and Galați.", Flag = new Uri(countries[41].flag), Population = countries[41].population, IsEUMember = true },
                new Country { Id = guids[42], Name = countries[42].name, Capital = countries[42].capital, Currency = countries[42].currencies[0].name, Description = "Russia, or the Russian Federation, is a transcontinental country located in Eastern Europe and Northern Asia. At 17,125,200 square kilometres (6,612,100 sq mi), it is, by a considerable margin, the largest country in the world by area, covering more than one-eighth of the Earth's inhabited land area, spanning eleven time zones, and bordering 16 sovereign nations. The territory of Russia extends from the Baltic Sea in the west to the Pacific Ocean in the east, and from the Arctic Ocean in the north to the Black Sea and the Caucasus in the south. With 146.7 million inhabitants living in the country's 85 federal subjects, Russia is the most populous nation in Europe and the ninth-most populous nation in the world. Russia's capital and largest city is Moscow; other major urban areas include Saint Petersburg, Novosibirsk, Yekaterinburg, Nizhny Novgorod, Kazan and Chelyabinsk.", Flag = new Uri(countries[42].flag), Population = countries[42].population, IsEUMember = true },
                new Country { Id = guids[43], Name = countries[43].name, Capital = countries[43].capital, Currency = countries[43].currencies[0].name, Description = "San Marino, officially the Republic of San Marino, also known as the Most Serene Republic of San Marino, is an enclave microstate in Southern Europe, on the northeastern side of the Apennine Mountains, completely surrounded by Italy.", Flag = new Uri(countries[43].flag), Population = countries[43].population, IsEUMember = true },
                new Country { Id = guids[44], Name = countries[44].name, Capital = countries[44].capital, Currency = countries[44].currencies[0].name, Description = "Serbia, officially the Republic of Serbia, is a landlocked country situated at the crossroads of Central and Southeast Europe in the southern Pannonian Plain and the central Balkans. It borders Hungary to the north, Romania to the northeast, Bulgaria to the southeast, North Macedonia to the south, Croatia and Bosnia and Herzegovina to the west, and Montenegro to the southwest. The country claims a border with Albania through the disputed territory of Kosovo. Serbia's population numbers approximately seven million. Its capital, Belgrade, ranks among the largest citiеs in southeastern Europe.", Flag = new Uri(countries[44].flag), Population = countries[44].population, IsEUMember = true },
                new Country { Id = guids[45], Name = countries[45].name, Capital = countries[45].capital, Currency = countries[45].currencies[0].name, Description = "Slovakia, officially the Slovak Republic, is a landlocked country in Central Europe. It is bordered by Poland to the north, Ukraine to the east, Hungary to the south, Austria to the southwest, and the Czech Republic to the northwest. Slovakia's territory spans about 49,000 square kilometres (19,000 sq mi) and is mostly mountainous. The population is over 5.6 million and consists mostly of Slovaks. The capital and largest city is Bratislava, and the second-largest city is Košice. The official language is Slovak.", Flag = new Uri(countries[45].flag), Population = countries[45].population, IsEUMember = true },
                new Country { Id = guids[46], Name = countries[46].name, Capital = countries[46].capital, Currency = countries[46].currencies[0].name, Description = "Slovenia, officially the Republic of Slovenia, is a country located in Europe at the crossroads of main European cultural and trade routes. It is bordered by Italy to the west, Austria to the north, Hungary to the northeast, Croatia to the southeast, and the Adriatic Sea to the southwest. Slovenia covers 20,273 square kilometers (7,827 sq mi) and has a population of 2.084 million. One of the successor states of the former Yugoslavia, Slovenia is now a parliamentary republic and member nation of the European Union, United Nations, and NATO. The capital and largest city is Ljubljana.", Flag = new Uri(countries[46].flag), Population = countries[46].population, IsEUMember = true },
                new Country { Id = guids[47], Name = countries[47].name, Capital = countries[47].capital, Currency = countries[47].currencies[0].name, Description = "Spain, officially the Kingdom of Spain, is a country in Southwestern Europe with some pockets of Spanish territory across the Strait of Gibraltar and the Atlantic Ocean. Its continental European territory is situated on the Iberian Peninsula. Its territory also includes two archipelagoes: the Canary Islands off the coast of Africa, and the Balearic Islands in the Mediterranean Sea. The African enclaves of Ceuta, Melilla, and Peñón de Vélez de la Gomera make Spain the only European country to have a physical border with an African country (Morocco). Several small islands in the Alboran Sea are also part of Spanish territory. The country's mainland is bordered to the south and east by the Mediterranean Sea except for a small land boundary with Gibraltar; to the north and northeast by France, Andorra, and the Bay of Biscay; and to the west and northwest by Portugal and the Atlantic Ocean.", Flag = new Uri(countries[47].flag), Population = countries[47].population, IsEUMember = true },
                new Country { Id = guids[48], Name = countries[48].name, Capital = countries[48].capital, Currency = countries[48].currencies[0].name, Description = "Svalbard and Jan Mayen is a statistical designation defined by ISO 3166-1 for a collective grouping of two remote jurisdictions of Norway: Svalbard and Jan Mayen. While the two are combined for the purposes of the International Organization for Standardization (ISO) category, they are not administratively related. This has further resulted in the country code top-level domain .sj being issued for Svalbard and Jan Mayen, and ISO 3166-2:SJ. The United Nations Statistics Division also uses this code, but has named it Svalbard and Jan Mayen Islands.", Flag = new Uri(countries[48].flag), Population = countries[48].population, IsEUMember = true },
                new Country { Id = guids[49], Name = countries[49].name, Capital = countries[49].capital, Currency = countries[49].currencies[0].name, Description = "Sweden, officially the Kingdom of Sweden, is a Scandinavian country in Northern Europe. It borders Norway to the west and north and Finland to the east, and is connected to Denmark in the southwest by a bridge-tunnel across the Öresund Strait. At 450,295 square kilometres (173,860 sq mi), Sweden is the largest country in Northern Europe, the third-largest country in the European Union and the fifth largest country in Europe by area. The capital city is Stockholm. Sweden has a total population of 10.3 million of which 2.6 million have a foreign background. Persons who have foreign backgrounds are defined as persons who are foreign born, or born in Sweden with foreign born parents. It has a low population density of 22 inhabitants per square kilometre (57/sq mi) and the highest urban concentration is in the central and southern half of the country.", Flag = new Uri(countries[49].flag), Population = countries[49].population, IsEUMember = true },
                new Country { Id = guids[50], Name = countries[50].name, Capital = countries[50].capital, Currency = countries[50].currencies[0].name, Description = "Switzerland, officially the Swiss Confederation, is a country situated in the confluence of Western, Central, and Southern Europe. It is a federal republic composed of 26 cantons, with federal authorities seated in Bern. Switzerland is a landlocked country bordered by Italy to the south, France to the west, Germany to the north, and Austria and Liechtenstein to the east. It is geographically divided among the Swiss Plateau, the Alps, and the Jura, spanning a total area of 41,285 km2 (15,940 sq mi), and land area of 39,997 km2 (15,443 sq mi). While the Alps occupy the greater part of the territory, the Swiss population of approximately 8.5 million is concentrated mostly on the plateau, where the largest cities are located, among them the two global cities and economic centres of Zürich and Geneva.", Flag = new Uri(countries[50].flag), Population = countries[50].population, IsEUMember = true },
                new Country { Id = guids[51], Name = countries[51].name, Capital = countries[51].capital, Currency = countries[51].currencies[0].name, Description = "Ukraine is a country in Eastern Europe. It is bordered by Russia to the north-east; Belarus to the north; Poland, Slovakia and Hungary to the west; and Romania, Moldova, and the Black Sea to the south. Ukraine is currently in a territorial dispute with Russia over the Crimean Peninsula, which Russia annexed in 2014. Including the Crimean Peninsula, Ukraine has an area of 603,628 km2 (233,062 sq mi), making it the second-largest country in Europe after Russia, and the 46th-largest country in the world. Excluding Crimea, Ukraine has a population of about 42 million, making it the eighth or ninth-most populous country in Europe and the 32nd-most populous country in the world. Its capital and largest city is Kiev. Ukrainian is the official language and its alphabet is Cyrillic. The dominant religion in the country is Eastern Orthodoxy.", Flag = new Uri(countries[51].flag), Population = countries[51].population, IsEUMember = true },
                new Country { Id = Guid.Parse("00000000-0000-0000-0000-000000000001"), Name = countries[52].name, Capital = countries[52].capital, Currency = countries[52].currencies[0].name, Description = "The United Kingdom of Great Britain and Northern Ireland, commonly known as the United Kingdom or Britain, is a sovereign country located off the north­western coast of the European mainland. The United Kingdom includes the island of Great Britain, the north­eastern part of the island of Ireland, and many smaller islands. Northern Ireland shares a land border with the Republic of Ireland. Otherwise, the United Kingdom is surrounded by the Atlantic Ocean, with the North Sea to the east, the English Channel to the south and the Celtic Sea to the southwest, giving it the 12th-longest coastline in the world. The Irish Sea separates Great Britain and Ireland. The total area of the United Kingdom is 94,000 square miles (240,000 km2).", Flag = new Uri(countries[52].flag), Population = countries[52].population, IsEUMember = true }
